select `user`.YH_USERNAME, detection_device.JCSB_NAME, detection_data.JCSJ_ID, detection_data.JCSJ_RECORD_TIME, detection_data.JCSJ_EXTRACT_TIME, detection_data.JCSJ_NO, detection_data.JCSJ_CONTROL_CHART_ID, detection_data.JCSJ_DATA_POINT_HIERARCHICA_DATA, detection_data.JCSJ_DATA_SAMPLE, detection_data.JCSJ_STATE, detection_data.JCSJ_CONTROL_CHART_DATA, detection_data.JCSJ_USER_ID, detection_data.JCSJ_DEVICE_ID, detection_data.JCSJ_EXTEND1, detection_data.JCSJ_EXTEND2, detection_data.JCSJ_EXTEND3, detection_data.JCSJ_EXTEND4, detection_data.JCSJ_EXTEND5, control_chart_infomation.KZTXX_SPECIFICATION_UPPER_LIMIT, control_chart_infomation.KZTXX_TARGET_VALUE, control_chart_infomation.KZTXX_SPECIFICATION_DOWN_LIMIT FROM detection_data left join user on `user`.YH_ID = detection_data.JCSJ_USER_ID left join detection_device on detection_device.JCSB_ID = detection_data.JCSJ_DEVICE_ID left join control_chart_infomation on detection_data.JCSJ_CONTROL_CHART_ID = control_chart_infomation.KZTXX_ID
where 1=1
and JCSJ_RECORD_TIME BETWEEN #{startTime1,jdbcType=BIGINT} AND #{endTime1,jdbcType=BIGINT}
and JCSJ_EXTRACT_TIME BETWEEN #{startTime2,jdbcType=BIGINT} AND #{endTime2,jdbcType=BIGINT}
and JCSJ_CONTROL_CHART_ID in (
#{id,jdbcType=INTEGER}
)
and JCSJ_CONTROL_CHART_ID = #{Id,jdbcType=INTEGER}
and JCSJ_DATA_POINT_HIERARCHICA_DATA = #{jcsjDataPointHierarchicaData,jdbcType=VARCHAR}
limit #{offset,jdbcType=INTEGER},#{limit,jdbcType=INTEGER}

